import React from 'react'
import { Card, CardBody, Heading, Text } from '@pancakeswap/uikit'
import styled from 'styled-components'
import { getBalanceNumber } from 'utils/formatBalance'
import { useTotalSupply, useBurnedBalance } from 'hooks/useTokenBalance'
import { useTranslation } from 'contexts/Localization'
import { getCakeAddress } from 'utils/addressHelpers'
import CardValue from './CardValue'

const StyledCakeStats = styled(Card)`
  margin-left: auto;
  margin-right: auto;
`

const Row = styled.div`
  align-items: center;
  display: flex;
  font-size: 14px;
  justify-content: space-between;
  margin-bottom: 8px;
`

const CakeStats = () => {
  const { t } = useTranslation()
  const totalSupply = useTotalSupply()
  const burnedBalance = getBalanceNumber(useBurnedBalance(getCakeAddress()))
  const cakeSupply = totalSupply ? getBalanceNumber(totalSupply) - burnedBalance : 0

  return (
    <StyledCakeStats>
      <CardBody>
        <Heading scale="xl" mb="24px">
          {t('TOCO Stats')}
        </Heading>
        <Row>
          <Text fontSize="14px">{t('Total TOCO Supply')}</Text>
          {cakeSupply && <CardValue fontSize="14px" value={1000000} />}
        </Row>
        <Row>
          <Text fontSize="14px">{t('Total TOCO Burned')}</Text>
          <CardValue fontSize="14px" decimals={0} value={500000} />
        </Row>
        <Row>
          <Text fontSize="14px">{t('New TOCO/block')}</Text>
          <CardValue fontSize="14px" decimals={0} value={5} />
        </Row>
      </CardBody>
    </StyledCakeStats>
  )
}

export default CakeStats
